admresource.script:

	function jsClear(frm, opt)
	{
	        switch(opt)
	        {
		        case '1':  	frm.btnadd.value='';
		         		break
		        case '2':  	frm.btnedit.value='';
		         		break				         		
		        case '3':   	frm.btnremove.value='';
		        		break
	        }
	}
