            if(!document.frmWBBanner.local[0].checked && !document.frmWBBanner.local[1].checked){
				document.frmWBBanner.local[1].checked=true;
            }
			if(!document.frmWBBanner.target[0].checked && !document.frmWBBanner.target[1].checked){
          		document.frmWBBanner.target[1].checked=true;
            }
			function jsValida(frm)
			{
                if(document.getElementById('local2').checked)
				{
                	if(document.getElementById('wbfile_img')==undefined && (document.getElementById('img').value==null || document.getElementById('img').value=='' || document.getElementById('img').value==' '))
					{
						alert('Especifique una imagen para el banner');
						document.getElementById('img').focus();
						return false;
					}
              	}
                if(document.getElementById('local1').checked)
				{
                   if(document.getElementById('code').value==null || document.getElementById('code').value=='')
					{
						alert('Defina la codificación para incluir la publicidad externa.');
						document.getElementById('code').focus();
						return false;
					}
				}
                return true;
			}
