admresource.script:
        
        dojo.require("dijit.form.Button");

        dojo.addOnLoad(function(){
            dojo.connect(dijit.byId("btnSave"), "onClick",
                function() {
                    if(jsValida(dojo.byId("frmWBBanner"))) {
                        dojo.byId("frmWBBanner").submit();
                    }
                }
            );
        });

        dojo.addOnLoad(function(){
            dojo.connect(dijit.byId("btnReset"), "onClick",
                function() {
                    dojo.byId("frmWBBanner").reset();
                }
            );
        });

		
           if(!document.getElementById('local1').checked && !document.getElementById('local2').checked)
             document.getElementById('local1').checked=true;
           if(!document.getElementById('target1').checked && !document.getElementById('target2').checked)
             document.getElementById('target1').checked=true;
           function jsValida(frm)
			{
                if(document.getElementById('local2').checked)
				{
                	if(document.getElementById('wbfile_img')==undefined && (document.getElementById('img').value==null || document.getElementById('img').value=='' || document.getElementById('img').value==' '))
					{
						alert('Especifique una imagen para el banner');
						document.getElementById('img').focus();
						return false;
					}
              	}
                if(document.getElementById('local1').checked)
				{
                   if(document.getElementById('code').value==null || document.getElementById('code').value=='')
					{
						alert('Defina la codificación para incluir la publicidad externa.');
						document.getElementById('code').focus();
						return false;
					}
				}
                return true;
			}
