admresource.script:
		
        dojo.require("dijit.form.Button");
        dojo.addOnLoad(function(){
            dojo.connect(dijit.byId("btnSave"), "onClick",
                function() {
                    if(jsValida(dojo.byId("frmIFrameContent"))) {
                        dojo.byId("frmIFrameContent").submit();
                    }
                }
            );
        });
        dojo.addOnLoad(function(){
            dojo.connect(dijit.byId("btnReset"), "onClick",
                function() {
                    dojo.byId("frmIFrameContent").reset();
                }
            );
        });

        function jsValida(frm) {
            if( frm.height.value.length>0 && isNaN(parseInt(frm.height.value, 10)) ) {
                alert('Altura deve ser numérico.');
                frm.height.focus();
                return false;
            }
            if(frm.height.value < 0) {
                alert("Altura deve ser maior que zero.");
                frm.height.focus();
                return false;
            }

            if( frm.marginwidth.value.length>0 && isNaN(parseInt(frm.marginwidth.value, 10)) ) {
                alert('Largura da margem deve ser numérico.');
                frm.marginwidth.focus();
                return false;
            }
            if(frm.marginwidth.value < 0) {
                alert("Largura da margem deve ser maior que zero.");
                frm.marginwidth.focus();
                return false;
            }

            if( frm.marginheight.value.length>0 && isNaN(parseInt(frm.marginheight.value, 10)) ) {
                alert('Altura da margem deve ser numérico.');
                frm.marginheight.focus();
                return false;
            }
            if(frm.marginheight.value < 0) {
                alert("Altura da margem deve ser maior que zero.");
                frm.marginheight.focus();
                return false;
            }

            return true;
        }
