admresource.script:
          

    function toggleLevel(frm) {        
        for (var i=0; i<frm.buildmode.length; i++) {
            if(frm.buildmode[i].checked && frm.buildmode[i].value=='recursive') {                        
                frm.level.value="";
                frm.level.disabled=true;
                break
            }else {
                frm.level.disabled=false;
            }
        }
    }
    
    function jsClear(frm)
    {
            frm.btntexto.value=''; 
            frm.lnktexto.value='';
    }
    
    function isNumber(pIn)
    {
       pCaracter=pIn.value;
       for (var i=0;i<pCaracter.length;i++)
       {
           var sByte=pCaracter.substring(i,i+1);
           if (sByte<"0" || sByte>"9")
           {
               pIn.focus();
               alert('Este campo debe ser numérico.');
               return false;
           }
       }
       return true;
    }
    
    function jsValida(frm)
    {
        if(!isNumber(frm.level)) {
            return false;
        }
        return true;
    }
