admresource.script:
          
    function jsValida(frm) {
        var level = parseInt(frm.level.value, 10);
        if(isNaN(level)) {
            alert('Este campo debe ser numérico.');
            return false;
        }
        if(level < 1) {
            alert("El nivel de despliegue debe ser mayor que cero.");
            return false;
        }
        return true;
    }
