package org.semanticwb.portal.social.facebook;

// TODO: Auto-generated Javadoc
/**
 * The Class Fb_User.
 * 
 * @author jorge.jimenez
 */
public class Fb_User {

    /** The name. */
    String name=null;
    
    /** The pic. */
    String pic=null;
    
    /** The city. */
    String city=null;
    
    /** The state. */
    String state=null;
    
    /** The country. */
    String country=null;
    
    /** The profile_url. */
    String profile_url=null;
    
    /** The pic_with_logo. */
    String pic_with_logo=null;
    
    /** The birthday. */
    String birthday=null;

    /**
     * Sets the name.
     * 
     * @param name the new name
     */
    public void setName(String name){
        this.name=name;
    }
    
    /**
     * Sets the pic.
     * 
     * @param pic the new pic
     */
    public void setPic(String pic){
        this.pic=pic;
    }
    
    /**
     * Sets the pic_with_logo.
     * 
     * @param pic_with_logo the new pic_with_logo
     */
    public void setPic_with_logo(String pic_with_logo){
        this.pic_with_logo=pic_with_logo;
    }
    
    /**
     * Sets the city.
     * 
     * @param city the new city
     */
    public void setCity(String city){
        this.city=city;
    }
    
    /**
     * Sets the state.
     * 
     * @param state the new state
     */
    public void setState(String state){
        this.state=state;
    }
    
    /**
     * Sets the country.
     * 
     * @param country the new country
     */
    public void setCountry(String country){
        this.country=country;
    }
    
    /**
     * Sets the profile_url.
     * 
     * @param profile_url the new profile_url
     */
    public void setProfile_url(String profile_url){
        this.profile_url=profile_url;
    }
    
    /**
     * Sets the birthday.
     * 
     * @param birthday the new birthday
     */
    public void setBirthday(String birthday){
        this.birthday=birthday;
    }

    //gets

    /**
     * Gets the name.
     * 
     * @return the name
     */
    public String getName(){
        return name;
    }

    /**
     * Gets the pic.
     * 
     * @return the pic
     */
    public String getPic(){
        return pic;
    }

    /**
     * Gets the pic_with_logo.
     * 
     * @return the pic_with_logo
     */
    public String getPic_with_logo(){
        return pic_with_logo;
    }

    /**
     * Gets the city.
     * 
     * @return the city
     */
    public String getCity(){
        return city;
    }

    /**
     * Gets the state.
     * 
     * @return the state
     */
    public String getState(){
        return state;
    }

    /**
     * Gets the country.
     * 
     * @return the country
     */
    public String getCountry(){
        return country;
    }

    /**
     * Gets the profile_url.
     * 
     * @return the profile_url
     */
    public String getProfile_url(){
        return profile_url;
    }

    /**
     * Gets the birthday.
     * 
     * @return the birthday
     */
    public String getBirthday(){
        return birthday;
    }
}
